import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Cliente } from '../interfaces/cliente';


@Injectable({
  providedIn: 'root'
})
export class ClienteService {
  clientes: Cliente[] = [];

  constructor(private http:HttpClient) { }

  getClientes(): Observable<Cliente[]> {
    const observable = new Observable<Cliente[]>(observer => {
      this.http.get<Cliente[]>('./assets/data/clientes.json');
    });

    //this.http.get<Cliente[]>('./assets/data/clientes.json')
    //this.clientes = this.http.get<Cliente[]>('./assets/data/clientes.json');
    //this.getCli();
    return observable
  }

  getCli(){
    this.http.get<Cliente[]>('./assets/data/clientes.json').subscribe(data =>{
      this.clientes = data;
  })

  }

  eliminarCliente(index: number){
    console.log("clientes=",this.clientes);

  }
}
