import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatSnackBar } from '@angular/material/snack-bar';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
  form: FormGroup;
  login = false;

  constructor(private fb: FormBuilder,private _snackBar: MatSnackBar) {
    this.form = this.fb.group({
      usuario:['',Validators.required],
      password:['',Validators.required]
    })
   }

  ngOnInit(): void {
  }

  ingresar() {
    console.log(this.form);
    //this.router.navigateByUrl('/dashboard');
    const usuario = this.form.value.usuario; 
    const password = this.form.value.password;

    if(usuario == 'jperez' && password == 'admin123'){

    }else{
      this.error();

    }
  }
  error(){
    this._snackBar.open('usuario o contraseña incorrectos','',{
      duration:5000,
      horizontalPosition:'center',
      verticalPosition:'bottom'
    })
  }
}
<div class="body">
    <div class="login">
        <mat-spinner *ngIf="!loading" class="spinner" diameter="50"></mat-spinner>
        <form [formGroup]="form" (ngSubmit)="ingresar()">
            <h1>Iniciar Sesion</h1>
            <mat-form-field class="ancho" appearance="fill">
                <mat-label>Usuario</mat-label>
                <input type="text" matInput autocomplete="off" formControlName="usuario">
              </mat-form-field>
              <mat-form-field class="ancho" appearance="fill">
                <mat-label>Contraseña</mat-label>
                <input type="password" matInput autocomplete="off" formControlName="password">
              </mat-form-field>
              <br>
              <button type="submit" class="ancho button" mat-raised-button color="primary" [disabled]="form.invalid" >Ingresar</button>
        </form>
    </div>
</div>
