import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatSnackBar } from '@angular/material/snack-bar';
import { Router } from '@angular/router';
import { Cliente } from 'src/app/interfaces/cliente';
import { ClienteService } from 'src/app/services/cliente.service';

@Component({
  selector: 'app-crear-cliente',
  templateUrl: './crear-cliente.component.html',
  styleUrls: ['./crear-cliente.component.css']
})
export class CrearClienteComponent implements OnInit {

  localidades: any[] = ['Lima','Arequipa','Cusco'];
  formulario: FormGroup;

  constructor(private fb:FormBuilder, private _clienteService: ClienteService, private router:Router, private _snackBar: MatSnackBar){
    this.formulario = this.fb.group({
      nombre:['',Validators.required],
      apellido:['',Validators.required],
      telefono:['',Validators.required],
      localidad:['',Validators.required]
    })
  }
  ngOnInit(){
  }

  registrarCliente(){
    console.log(this.formulario);
    const cliente: Cliente = {
      nombre: this.formulario.value.nombre,
      apellido: this.formulario.value.apellido,
      telefono: this.formulario.value.telefono,
      localidad: this.formulario.value.localidad,
      estado: 'Activado'
    }
    console.log("cliente",cliente);

    this._clienteService.agregarCliente(cliente);
    this.router.navigate(['/main/registro']);

    this._snackBar.open('Cliente registrado correctamente','',{
      duration:5000,
      horizontalPosition:'center',
      verticalPosition:'bottom'
    })
  }


}
<div class="container">
    <mat-toolbar>
        <span>Crear Cliente</span>
        <div class="example-spacer"></div>
    </mat-toolbar>
    <mat-card>
        <mat-card-content>
            <form [formGroup]="formulario" (ngSubmit)="registrarCliente()">
                <mat-grid-list cols="4" rowHeight="70px">
                    <mat-grid-tile [colspan]="1" [rowspan]="1">
                            <mat-form-field class="ancho">
                                <mat-label>Nombre</mat-label>
                                <input matInput autocomplete="off" formControlName="nombre">
                            </mat-form-field>
                    </mat-grid-tile>
                    <mat-grid-tile [colspan]="3" [rowspan]="5" >
                      <img src="./assets/img/Clientes.png" width="500">
                    </mat-grid-tile>

                    <mat-grid-tile [colspan]="1" [rowspan]="1">
                        <mat-form-field class="ancho">
                            <mat-label>Apellido</mat-label>
                            <input matInput autocomplete="off" formControlName="apellido">
                        </mat-form-field>
                    </mat-grid-tile>



                    <mat-grid-tile [colspan]="1" [rowspan]="1">
                        <mat-form-field class="ancho">
                            <mat-label>Telefono</mat-label>
                            <input matInput autocomplete="off" formControlName="telefono">
                        </mat-form-field>
                    </mat-grid-tile>

                    <mat-grid-tile [colspan]="1" [rowspan]="1">
                        <mat-form-field class="ancho">
                            <mat-label>Localidad</mat-label>
                            <mat-select formControlName="localidad">
                              <mat-option *ngFor="let item of localidades" [value]="item">
                                {{item}}
                              </mat-option>
                            </mat-select>
                          </mat-form-field>
                    </mat-grid-tile>

                    <mat-grid-tile [colspan]="1" [rowspan]="1">
                        <button mat-raised-button color="warn" style="margin-right: 2%;">Volver</button>
                        <button mat-raised-button color="primary" type="submit" [disabled]="formulario.invalid">Registrar</button>
                    </mat-grid-tile>
                </mat-grid-list>
                <br>
                <br>
            </form>
        </mat-card-content>
    </mat-card>
</div>
