import {Component, OnInit, ViewChild } from '@angular/core';
import {MatTableDataSource, MatTableModule} from '@angular/material/table';
import { TableUtil } from "./TableUtil";
import { Cliente } from 'src/app/interfaces/cliente';
import { ClienteService } from 'src/app/services/cliente.service';
import { MatSnackBar } from '@angular/material/snack-bar';
import { MatPaginator } from '@angular/material/paginator';

@Component({
  selector: 'app-registro',
  templateUrl: './registro.component.html',
  styleUrls: ['./registro.component.css']
})
export class RegistroComponent implements OnInit{

  listClientes: Cliente[] = [];
  dataSource!:MatTableDataSource<any>;

  displayedColumns: string[] = ['Nombre', 'Apellido','Telefono','Localidad', 'Estado','Acciones'];

  @ViewChild(MatPaginator) paginator!: MatPaginator;

  constructor(private _clienteService: ClienteService,private _snackBar: MatSnackBar){}

  ngOnInit(): void {
    this.cargarClientes();
  }

  cargarClientes(){
    var datosClientes = JSON.parse(localStorage.getItem("clientes")!) ;
    if(datosClientes!=null){
      this.dataSource = new MatTableDataSource(datosClientes);
    }else{
      this._clienteService.getClientes().subscribe(data =>{
        //console.log(data);
        this.dataSource = new MatTableDataSource(data);
        localStorage.setItem("clientes", JSON.stringify(data));
      })
    }
  }

  ngAfterViewInit() {
    this.dataSource.paginator = this.paginator;
  }

  exportTable(){
    TableUtil.exportToPdf("ExampleTable");
  }

  eliminarCliente(index: number){
    console.log(index);
    this._clienteService.eliminarCliente(index);
    this.cargarClientes();
    this._snackBar.open('Cliente eliminado correctamente','',{
      duration:5000,
      horizontalPosition:'center',
      verticalPosition:'bottom'
    })
  }

  actualizarCliente(indice: number){
    //var datosClientes = JSON.parse(localStorage.getItem("clientes")!) ;
    localStorage.setItem("indexActualizarCliente", ""+indice);
    /*datosClientes.forEach(function (value: any, index: any) {
      if(indice == index){
        localStorage.setItem("indexActualizarCliente", JSON.stringify(value));
      } 
    }); */
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.dataSource.filter = filterValue.trim().toLowerCase();
/*
    if (this.dataSource.paginator) {
      this.dataSource.paginator.firstPage();
    } */
  }
}
<div class="container">
    <mat-toolbar>
        <span>Clientes</span>
        <div class="example-spacer"></div>
        <button mat-raised-button color="primary" routerLink="/main/crear-cliente">+ Agregar Nuevo Cliente</button>
    </mat-toolbar>
    <br>
    <mat-form-field>
      <mat-label>Filtrar </mat-label>
      <input matInput autocomplete="off" (keyup)="applyFilter($event)" placeholder="Ex. Mia" #input>
    </mat-form-field>
    <table id="ExampleTable" mat-table [dataSource]="dataSource" class="mat-elevation-z4">

        <!--- Note that these columns can be defined in any order.
              The actual rendered columns are set as a propertwy on the row definition" -->

        <!-- Position Column -->
        <ng-container matColumnDef="Nombre">
          <th mat-header-cell *matHeaderCellDef style="text-align: center;"> Nombre </th>
          <td mat-cell *matCellDef="let element" style="text-align: center;"> {{element.nombre}} </td>
        </ng-container>

        <!-- Name Column -->
        <ng-container matColumnDef="Apellido">
          <th mat-header-cell *matHeaderCellDef style="text-align: center;"> Apellido </th>
          <td mat-cell *matCellDef="let element" style="text-align: center;"> {{element.apellido}} </td>
        </ng-container>


        <ng-container matColumnDef="Telefono">
            <th mat-header-cell *matHeaderCellDef style="text-align: center;"> Telefono </th>
            <td mat-cell *matCellDef="let element" style="text-align: center;"> {{element.telefono}} </td>
          </ng-container>

          <ng-container matColumnDef="Estado">
            <th mat-header-cell *matHeaderCellDef style="text-align: center;"> Estado </th>
            <td mat-cell *matCellDef="let element" style="text-align: center;"> {{element.estado}} </td>
          </ng-container>

          <ng-container matColumnDef="Localidad" >
            <th mat-header-cell *matHeaderCellDef style="text-align: center;"> Localidad </th>
            <td mat-cell *matCellDef="let element" style="text-align: center;"> {{element.localidad}} </td>
          </ng-container>

          <ng-container matColumnDef="Acciones">
            <th mat-header-cell *matHeaderCellDef style="text-align: center;"> Acciones </th>
            <td mat-cell *matCellDef="let element; let i = index" style="text-align: center;">
                <!-- <a class="pointer" style="color: #1878b0;" matTooltip="Consultar">
                    <mat-icon>search</mat-icon>
                </a> -->
                <a class="pointer" style="color: #1f516f;" matTooltip="Editar" routerLink="/main/actualizar-cliente" (click)="actualizarCliente(i)">
                    <mat-icon>edit</mat-icon>
                </a>
                <a class="pointer" style="color: #e15639;" matTooltip="Eliminar" (click)="eliminarCliente(i)">
                    <mat-icon>delete</mat-icon>
                </a>
            </td>
          </ng-container>

        <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
        <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
      </table>
      <mat-paginator [pageSizeOptions]="[5, 10, 20]"
      showFirstLastButtons
      aria-label="Select page of periodic elements">
    </mat-paginator>  
</div>
<div class="export-container">
	<button mat-raised-button color="primary" (click)="exportTable()">Export</button>
</div>
